mapper.select:
SELECT
id, title, writer, view_count, like_count, created_at
FROM post

    gallery_id = #{galleryId}



                AND (title LIKE CONCAT('%', #{searchCond.keyword}, '%') OR content LIKE CONCAT('%', #{searchCond.keyword}, '%'))


                AND title LIKE CONCAT('%', #{searchCond.keyword}, '%')


                AND content LIKE CONCAT('%', #{searchCond.keyword}, '%')


                AND writer = #{searchCond.keyword}




ORDER BY id DESC
LIMIT #{pageable.offset}, #{pageable.pageSize}

SELECT count(id) FROM post
gallery_id = #{galleryId}
AND (title LIKE CONCAT('%', #{searchCond.keyword}, '%') OR content LIKE CONCAT('%', #{searchCond.keyword}, '%'))
AND title LIKE CONCAT('%', #{searchCond.keyword}, '%')
AND content LIKE CONCAT('%', #{searchCond.keyword}, '%')
AND writer = #{searchCond.keyword}
